import React, { useState } from 'react';
import { Input } from 'semantic-ui-react';

const Search = props => {
  const [text, setText] = useState('');

  const onSubmit = e => {
    e.preventDefault();
    props.search(text);
    setText('');
  };

  const onChange = e => {
    setText(e.target.value);
  };

  return (
    <div>
      <form onSubmit={onSubmit} className='form'>
        <Input />
        <input
          type='text'
          name='text'
          placeholder='Search for qoutes regarding...'
          value={text}
          onChange={onChange}
          className='search'
        />
        <input
          type='submit'
          value='Search'
          className='btn btn-dark btn-block'
        />
      </form>
    </div>
  );
};

export default Search;
