import React, { useState } from 'react';

const Search = props => {
  const [text, setText] = useState('');

  const onSubmit = e => {
    e.preventDefault();
    props.searchCountries(text);
  };

  const onChange = e => {
    setText(e.target.value);
  };

  return (
    <div>
      <form onSubmit={onSubmit}>
        <input
          type='text'
          name='text'
          placeholder='Search'
          value={text}
          onChange={onChange}
        />
        <input
          type='submit'
          value='Search'
          className='btn btn-dark btn-block'
        />
      </form>
    </div>
  );
};

export default Search;
